mapper.update:
UPDATE NOTICE
SET VIEW_CNT = NVL(VIEW_CNT, 0) + 1
WHERE NTC_NO = #{value}

mapper.insert:
INSERT INTO NOTICE (
    NTC_NO 
  , NTC_TITLE
  , NTC_CONTENT
  , REG_ID
  , REG_DT
  , NTC_TYPE
  , VIEW_CNT
  , ATTACH_NO
  , STATUS
) VALUES (
    SEQ_NOTICE.NEXTVAL
  , #{ntcTitle}
  , #{ntcContent}
  , #{regId}
  , SYSDATE
  , #{ntcType}
  , 0
  , #{attachNo}
  , 'PUBLISHED'
)

mapper.update:
UPDATE NOTICE
SET STATUS = 'y'
WHERE NTC_NO = #{value}
