mapper.insert:
INSERT INTO ACCOMMODATION (
      ACC_NO
    , TRIP_PROD_NO 
    , API_CONTENT_ID  
    , ACC_CAT_CD
    , STAR_GRADE
    , ZIP
    , AREA_CODE
    , SIGUNGU_CODE
    , ADDR1
    , ADDR2
    , TOTAL_ROOM_CNT
    , ACC_NAME
    , ACC_FILE_PATH
    , MAPX
    , MAPY
    , LDONG_REGN_CD
    , LDONG_SIGNGU_CD
) VALUES (
      SEQ_ACCOMMODATION.NEXTVAL
    , SEQ_TRIP_PROD_NO.NEXTVAL
    , #{apiContentId}
    , #{accCatCd}
    , #{starGrade}
    , #{zip}
    , #{areaCode}
    , #{sigunguCode}
    , #{addr1}
    , #{addr2}
    , #{totalRoomCnt}
    , #{accName}
    , #{accFilePath}
    , #{mapx}
    , #{mapy}
    , #{ldongRegnCd}
    , #{ldongSignguCd}
)

mapper.select:
SELECT COUNT(*)
FROM ACCOMMODATION
WHERE API_CONTENT_ID = #{contentId}

SELECT * FROM ( SELECT ROWNUM AS RN, PAGING.* FROM ( SELECT A.ACC_NO , A.ACC_NAME , A.ADDR1 , A.ACC_CAT_CD , A.ACC_FILE_PATH , A.STAR_GRADE , AF.WIFI_YN , AF.PARKING_YN , AF.BREAKFAST_YN , AF.POOL_YN , AF.GYM_YN , AF.SPA_YN , AF.RESTAURANT_YN , AF.BAR_YN , AF.ROOM_SERVICE_YN , AF.LAUNDRY_YN , AF.SMOKING_AREA_YN , AF.PET_FRIENDLY_YN , R.ROOM_TYPE_NO , R.ROOM_NAME , R.BASE_GUEST_COUNT , R.PRICE , R.DISCOUNT , R.ROOM_SIZE , (SELECT MIN(PRICE) FROM ROOM_TYPE RT WHERE RT.ACC_NO = A.ACC_NO) AS MIN_PRICE , (SELECT MAX(DISCOUNT) FROM ROOM_TYPE RT WHERE RT.ACC_NO = A.ACC_NO) AS MAX_DISCOUNT , (SELECT MIN(PRICE * (100 - NVL(DISCOUNT, 0)) / 100) FROM ROOM_TYPE RT WHERE RT.ACC_NO = A.ACC_NO) AS FINAL_PRICE FROM ACCOMMODATION A LEFT OUTER JOIN ACC_FACILITY AF ON A.ACC_NO = AF.ACC_NO LEFT OUTER JOIN ROOM_TYPE R ON A.ACC_NO = R.ACC_NO
AND A.ACC_NO = #{accNo}
AND A.AREA_CODE = #{areaCode}
AND (A.ACC_NAME LIKE '%' || #{keyword} || '%' OR A.ADDR1 LIKE '%' || #{keyword} || '%')
AND EXISTS ( SELECT 1 FROM ROOM_TYPE RT WHERE RT.ACC_NO = A.ACC_NO AND RT.MAX_GUEST_COUNT >= #{adultCount} )
ORDER BY A.ACC_NO DESC, (R.PRICE * (100 - NVL(R.DISCOUNT, 0))/100) ASC, R.ROOM_TYPE_NO ASC ) PAGING ) WHERE RN BETWEEN #{startRow} AND #{endRow}

	    SELECT COUNT(*) 
        FROM ACCOMMODATION A


		        AND A.ACC_NO = #{accNo}




		            AND A.AREA_CODE = #{areaCode}


		            AND (A.ACC_NAME LIKE '%' || #{keyword} || '%' OR A.ADDR1 LIKE '%' || #{keyword} || '%')




		        AND EXISTS (
		            SELECT 1 FROM ROOM_TYPE RT 
		            WHERE RT.ACC_NO = A.ACC_NO 
		            AND RT.MAX_GUEST_COUNT >= #{adultCount}
		        )

mapper.update:
MERGE INTO ACC_FACILITY AF
USING DUAL ON (AF.ACC_NO = #{accNo})
WHEN MATCHED THEN
    UPDATE SET 
        WIFI_YN = #{wifiYn}
        , PARKING_YN = #{parkingYn}
        , BREAKFAST_YN = #{breakfastYn}
        , POOL_YN = #{poolYn}
        , GYM_YN = #{gymYn}
        , SPA_YN = #{spaYn}
        , RESTAURANT_YN = #{restaurantYn}
        , BAR_YN = #{barYn}
        , ROOM_SERVICE_YN = #{roomServiceYn}
        , LAUNDRY_YN = #{laundryYn}
        , SMOKING_AREA_YN = #{smokingAreaYn}
        , PET_FRIENDLY_YN = #{petFriendlyYn}
WHEN NOT MATCHED THEN
    INSERT (
        ACC_NO
        , WIFI_YN
        , PARKING_YN
        , BREAKFAST_YN
        , POOL_YN
        , GYM_YN
        , SPA_YN
        , RESTAURANT_YN
        , BAR_YN
        , ROOM_SERVICE_YN
        , LAUNDRY_YN
        , SMOKING_AREA_YN
        , PET_FRIENDLY_YN
    ) VALUES (
        #{accNo}
        , #{wifiYn}
        , #{parkingYn}
        , #{breakfastYn}
        , #{poolYn}
        , #{gymYn}
        , #{spaYn}
        , #{restaurantYn}
        , #{barYn}
        , #{roomServiceYn}
        , #{laundryYn}
        , #{smokingAreaYn}
        , #{petFriendlyYn}
    )

mapper.insert:
INSERT INTO ROOM_TYPE (
    ACC_NO
    , ROOM_NAME
    , BASE_GUEST_COUNT
    , MAX_GUEST_COUNT
    , BED_COUNT
    , BED_TYPE_CD
    , TOTAL_ROOM_COUNT
    , PRICE
    , DISCOUNT
    , EXTRA_GUEST_FEE
    , ROOM_SIZE
    , BREAKFAST_YN
) VALUES (
   #{accNo}
    #{roomName}
    , #{baseGuestCount}
    , #{maxGuestCount}
    , #{bedCount}
    , #{bedTypeCd}
    , #{totalRoomCount}
    , #{price}
    , #{discount}
    , #{extraGuestFee}
    , #{roomSize}
    , #{breakfastYn}
)

INSERT INTO ROOM_FACILITY (
