    , AIR_CON_YN
    , TV_YN
    , MINIBAR_YN
    , FRIDGE_YN
    , SAFE_BOX_YN
    , HAIR_DRYER_YN
    , BATHTUB_YN
    , TOILETRIES_YN
) VALUES (
    #{roomTypeNo}
    , #{airConYn}
    , #{tvYn}
    , #{minibarYn}
    , #{fridgeYn}
    , #{safeBoxYn}
    , #{hairDryerYn}
    , #{bathtubYn}
    , #{toiletriesYn}
)

INSERT INTO ROOM_FEATURE (
    ROOM_TYPE_NO
    , FREE_CANCEL_YN
    , OCEAN_VIEW_YN
    , MOUNTAIN_VIEW_YN
    , CITY_VIEW_YN
    , LIVING_ROOM_YN
    , TERRACE_YN
    , NON_SMOKING_YN
    , KITCHEN_YN
) VALUES (
    #{roomTypeNo}
    , #{freeCancelYn}
    , #{oceanViewYn}
    , #{mountainViewYn}
    , #{cityViewYn}
    , #{livingRoomYn}
    , #{terraceYn}
    , #{nonSmokingYn}
    , #{kitchenYn}
)

INSERT INTO ROOM (
    ROOM_INFO_NO, ROOM_TYPE_NO, USE_YN
) VALUES (
    SEQ_ROOM.NEXTVAL, #{roomTypeNo}, 'Y'
)
