mapper.insert:
INSERT INTO ACCOMMODATION (
      ACC_NO
    , TRIP_PROD_NO 
    , API_CONTENT_ID  
    , ACC_CAT_CD
    , STAR_GRADE
    , ZIP
    , AREA_CODE
    , SIGUNGU_CODE
    , ADDR1
    , ADDR2
    , TOTAL_ROOM_CNT
    , ACC_NAME
    , ACC_FILE_PATH
    , MAPX
    , MAPY
    , LDONG_REGN_CD
    , LDONG_SIGNGU_CD
) VALUES (
      SEQ_ACCOMMODATION.NEXTVAL
    , SEQ_TRIP_PROD_NO.NEXTVAL
    , #{apiContentId}
    , #{accCatCd}
    , #{starGrade}
    , #{zip}
    , #{areaCode}
    , #{sigunguCode}
    , #{addr1}
    , #{addr2}
    , #{totalRoomCnt}
    , #{accName}
    , #{accFilePath}
    , #{mapx}
    , #{mapy}
    , #{ldongRegnCd}
    , #{ldongSignguCd}
)

mapper.select:
SELECT COUNT(*)
FROM ACCOMMODATION
WHERE API_CONTENT_ID = #{contentId}

SELECT * FROM ( SELECT ROWNUM AS RNUM, T.* FROM ( SELECT A.ACC_NO , A.ACC_NAME , A.ADDR1 , A.ACC_FILE_PATH , A.STAR_GRADE , (SELECT MIN(PRICE * (100 - NVL(DISCOUNT, 0)) / 100) FROM ROOM_TYPE RT WHERE RT.ACC_NO = A.ACC_NO) AS FINAL_PRICE FROM ACCOMMODATION A
AND A.AREA_CODE = #{areaCode}
AND (A.ACC_NAME LIKE '%' || #{keyword} || '%' OR A.ADDR1 LIKE '%' || #{keyword} || '%')
ORDER BY A.ACC_NO DESC ) T WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}

SELECT COUNT(*) FROM ACCOMMODATION


        AND AREA_CODE = #{areaCode}


        AND (ACC_NAME LIKE '%' || #{keyword} || '%' OR ADDR1 LIKE '%' || #{keyword} || '%')

mapper.update:
UPDATE ACCOMMODATION
SET 
    OVERVIEW = #{overview},
    TEL = #{tel}
WHERE ACC_NO = #{accNo}

MERGE INTO ACC_FACILITY AF
USING DUAL ON (AF.ACC_NO = #{accNo})
WHEN MATCHED THEN
    UPDATE SET 
        WIFI_YN = #{wifiYn}
        , PARKING_YN = #{parkingYn}
        , BREAKFAST_YN = #{breakfastYn}
        , POOL_YN = #{poolYn}
        , GYM_YN = #{gymYn}
        , SPA_YN = #{spaYn}
        , RESTAURANT_YN = #{restaurantYn}
        , BAR_YN = #{barYn}
        , ROOM_SERVICE_YN = #{roomServiceYn}
        , LAUNDRY_YN = #{laundryYn}
        , SMOKING_AREA_YN = #{smokingAreaYn}
        , PET_FRIENDLY_YN = #{petFriendlyYn}
WHEN NOT MATCHED THEN
    INSERT (
        ACC_NO
        , WIFI_YN
        , PARKING_YN
        , BREAKFAST_YN
        , POOL_YN
        , GYM_YN
        , SPA_YN
        , RESTAURANT_YN
        , BAR_YN
        , ROOM_SERVICE_YN
        , LAUNDRY_YN
        , SMOKING_AREA_YN
        , PET_FRIENDLY_YN
    ) VALUES (
        #{accNo}
        , #{wifiYn}
        , #{parkingYn}
        , #{breakfastYn}
        , #{poolYn}
        , #{gymYn}
        , #{spaYn}
        , #{restaurantYn}
        , #{barYn}
        , #{roomServiceYn}
        , #{laundryYn}
        , #{smokingAreaYn}
        , #{petFriendlyYn}
    )

mapper.insert:
INSERT INTO ROOM_TYPE (
    ACC_NO
    , ROOM_NAME
    , BASE_GUEST_COUNT
    , MAX_GUEST_COUNT
    , PRICE
    , ROOM_SIZE
    , DISCOUNT
) VALUES (
    #{accNo}
    , #{roomName}
    , #{baseGuestCount}
    , #{maxGuestCount}
    , #{price}
    , #{roomSize}
    , 0
)

INSERT INTO ROOM_FACILITY (
    ROOM_TYPE_NO
    , AIR_CON_YN
    , TV_YN
    , MINIBAR_YN
    , FRIDGE_YN
    , SAFE_BOX_YN
    , HAIR_DRYER_YN
    , BATHTUB_YN
    , TOILETRIES_YN
) VALUES (
    #{roomTypeNo}
    , #{airConYn}
    , #{tvYn}
    , 'N'
    , #{fridgeYn}
    , 'N'
    , #{hairDryerYn}
    , 'N'
    , #{toiletriesYn}
)

