mapper.select:
SELECT a.ACC_NO , a.TRIP_PROD_NO , a.ACC_NAME , a.ACC_CAT_CD , a.STAR_GRADE , a.ADDR1 , a.ADDR2 , a.AREA_CODE , a.SIGUNGU_CODE , a.MAPX AS "mapx" , a.MAPY AS "mapy" , a.TEL , a.CHECK_IN_TIME , a.CHECK_OUT_TIME , a.OVERVIEW
, tp.TRIP_PROD_TITLE , tp.APRV_YN AS "aprvYn" , tp.APPROVE_STATUS AS "approveStatus" , tp.SALE_START_DT , tp.SALE_END_DT , tp.APRV_YN , TO_CHAR(tp.REG_DT, 'YYYY-MM-DD') AS "regDt" , tp.DEL_YN AS "delYn" , tp.CTY_NM AS AREA_CODE_PROD
FROM TRIP_PROD tp INNER JOIN ACCOMMODATION a ON tp.TRIP_PROD_NO = a.TRIP_PROD_NO WHERE tp.TRIP_PROD_NO = #{tripProdNo}

mapper.update:
UPDATE TRIP_PROD
SET 
      DEL_YN = #{delYn}      
    , MOD_DT = SYSDATE
WHERE TRIP_PROD_NO = #{tripProdNo}

UPDATE TRIP_PRODUCT
SET SALE_START_DT = #{saleStartDt},
    SALE_END_DT = #{saleEndDt}
WHERE TRIP_PROD_NO = #{tripProdNo}

mapper.insert:
INSERT INTO ROOM_TYPE (
    ACC_NO, ROOM_NAME, PRICE, DISCOUNT, 
    BASE_GUEST_COUNT, MAX_GUEST_COUNT, SIZE, BREAKFAST_YN
) VALUES (
    #{accNo}, #{roomName}, #{price}, #{discount}, 
    #{baseGuestCount}, #{maxGuestCount}, #{size}, #{breakfastYn}
)

mapper.update:
UPDATE TRIP_PRODUCT
SET DEL_YN = 'Y'
WHERE TRIP_PROD_NO = #{tripProdNo}
