mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, a.* FROM ( SELECT t.TRIP_PROD_NO, t.COMP_NO, t.PROD_CTGRY_TYPE, t.APPROVE_STATUS, t.TRIP_PROD_TITLE, t.TRIP_PROD_CONTENT, t.SALE_START_DT, t.SALE_END_DT, t.ATTACH_NO, t.VIEW_CNT, t.REG_DT, t.APRV_YN, t.DEL_YN, t.CTY_NM, s.PRICE, s.NETPRC, s.DISCOUNT, s.LEAD_TIME, s.CUR_STOCK, i.PROD_MIN_PEOPLE, i.PROD_MAX_PEOPLE, (SELECT AFD.FILE_PATH FROM ATTACH_FILE_DETAIL AFD WHERE AFD.ATTACH_NO = t.ATTACH_NO AND AFD.USE_YN = 'Y' AND ROWNUM = 1) AS THUMB_IMAGE, CASE WHEN t.APRV_YN = 'Y' AND t.APPROVE_STATUS = '판매중' THEN '판매중' WHEN t.APRV_YN = 'Y' THEN '판매중지' ELSE '승인대기' END AS DISPLAY_STATUS FROM TRIP_PROD t LEFT JOIN TRIP_PROD_SALE s ON t.TRIP_PROD_NO = s.TRIP_PROD_NO LEFT JOIN TRIP_PROD_INFO i ON t.TRIP_PROD_NO = i.TRIP_PROD_NO WHERE t.PROD_CTGRY_TYPE != 'accommodation' AND (t.DEL_YN = 'N' OR t.DEL_YN IS NULL)
AND ( t.TRIP_PROD_TITLE LIKE '%' || #{searchWord} || '%' OR t.TRIP_PROD_CONTENT LIKE '%' || #{searchWord} || '%' )
AND t.PROD_CTGRY_TYPE = #{searchType}
AND t.CTY_NM = #{searchRegion}
AND t.APRV_YN = 'Y' AND t.APPROVE_STATUS = '판매중'
AND t.APRV_YN = 'Y' AND t.APPROVE_STATUS = '판매중지'
AND (t.APRV_YN = 'N' OR t.APRV_YN IS NULL)
ORDER BY CASE WHEN t.APRV_YN = 'N' OR t.APRV_YN IS NULL THEN 0 ELSE 1 END, t.REG_DT DESC, t.TRIP_PROD_NO DESC ) a
WHERE ROWNUM <= #{currentPage} * #{screenSize}
)
WHERE rn > (#{currentPage} - 1) * #{screenSize}
