mapper.insert:

        SELECT SEQ_CHAT_ROOM.NEXTVAL FROM DUAL

		INSERT INTO CHAT_ROOM (
				  CHAT_ID
				  , CHAT_NAME
				  , CHAT_CTGRY
				  , CHAT_MAX
				  , REG_ID
				  , REG_DT
		) VALUES (
					#{chatId}
				  , #{chatName}
				  , #{chatCtgry}
				  , #{chatMax}
				  , #{regId}
				  , SYSDATE	
		)

INSERT INTO CHAT_USER (
		  CHAT_ID
		  , CHAT_USER_NO
		  , JOINED_AT
		  , CHAT_ROLE
		  , CHAT_STATUS
) VALUES (
		 #{chatId}
		  , #{chatUserNo}
		  , SYSDATE
		  , 'HOST'
		  , 'ONLINE'
)

mapper.select:
SELECT R.CHAT_ID AS chatId , R.CHAT_NAME AS chatName , R.CHAT_CTGRY AS chatCtgry , C.CD_NAME AS chatCtgryName , R.CHAT_MAX AS chatMax , NVL(CNT.USER_CNT, 0) AS currentUsers , R.REG_ID AS createdById , U.NICKNAME AS createdByNickname FROM CHAT_ROOM R LEFT JOIN CODE C ON R.CHAT_CTGRY = C.CD AND C.CDGR = 'CHAT_CATEGORY' LEFT JOIN ( SELECT CHAT_ID, COUNT(*) AS USER_CNT FROM CHAT_USER GROUP BY CHAT_ID ) CNT ON R.CHAT_ID = CNT.CHAT_ID LEFT JOIN MEMBER M ON R.REG_ID = M.MEM_ID LEFT JOIN MEM_USER U ON M.MEM_NO = U.MEM_NO
R.CHAT_CTGRY = #{category}
ORDER BY R.REG_DT DESC

SELECT COUNT(*)
FROM CHAT_USER
WHERE CHAT_ID = #{chatId}
  AND CHAT_USER_NO = #{memNo}

SELECT COUNT(*)
FROM CHAT_USER
WHERE CHAT_ID = #{chatId}

SELECT CHAT_MAX
FROM CHAT_ROOM
WHERE CHAT_ID = #{chatId}

mapper.update:
UPDATE CHAT_USER
SET CHAT_STATUS = 'OFFLINE'
WHERE CHAT_ID = #{chatId} 
AND CHAT_USER_NO = #{memNo}

mapper.delete:
DELETE FROM CHAT_USER
WHERE CHAT_ID = #{chatId}
  AND CHAT_USER_NO = #{memNo}

mapper.insert:
    INSERT INTO CHAT (
