mapper.insert:

	select seq_airport.nextval from dual

insert into airport(
	airport_no, airport_nm, airport_id, city_name
)values(
	#{airportNo}, #{airportNm}, #{airportId}, #{airportNm}
)

mapper.select:
select
	m.mem_no, m.mem_name, m.mem_email, m.point, mu.tel
from member m left outer join mem_user mu on(m.mem_no = mu.mem_no)
where mem_id = #{memId}

mapper.insert:
	INSERT INTO flight_product (
	    flt_prod_id, flight_symbol, dep_airport_id, arr_airport_id
	    , dep_time, arr_time, domestic_days, start_dt, end_dt
	    , checked_baggage, carry_on_baggage, extra_baggage_price, airline_id
	) VALUES (
	    #{fltProdId}, #{flightSymbol}, #{depAirportId}, #{arrAirportId},
        #{depTime}, #{arrTime}, #{domesticDays}, #{startDt}, #{endDt},
        #{checkedBaggage}, #{carryOnBaggage}, #{extraBaggagePrice}, #{airlineId}
	)

INSERT INTO flight_reservation (
    reserve_no, mem_no, pay_no, flt_prod_id, total_price
) VALUES (
    #{reserveNo}, #{memNo}, #{payNo}, #{fltProdId}, #{totalPrice}
)

INSERT INTO flight_passengers (
    passenger_id, last_name, first_name, birth_date
    , gender, passengers_type, baggage_price
    , extra_baggage, flight_seat, reserve_no
) VALUES (
	#{passengerId}, #{lastName}, #{firstName}, #{birthDate}
    , #{gender}, #{passengersType}, #{baggagePrice}
    , #{extraBaggage}, #{flightSeat}, #{reserveNo}
)
