mapper.select:
SELECT MEM_NO
FROM MEMBER
WHERE MEM_ID = #{memId}

SELECT COUNT(*)
FROM LIKES
WHERE LIKES_KEY = #{likesKey}
AND LIKES_CAT_CD = #{likesCatCd}
AND MEM_NO = #{memNo}

mapper.delete:
DELETE FROM LIKES
WHERE LIKES_KEY = #{likesKey}
AND LIKES_CAT_CD = #{likesCatCd}
AND MEM_NO = #{memNo}

mapper.select:
SELECT COUNT(*)
FROM LIKES
WHERE LIKES_KEY = #{likesKey}
AND LIKES_CAT_CD = #{likesCatCd}
