mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, a.* FROM ( SELECT s.SALE_NO, s.PROD_LIST_NO, s.SALE_DT, s.NET_SALES, s.SETTLE_STAT_CD, tpl.PAY_NO, tpl.PAY_PRICE, tpl.QUANTITY, tpl.RESV_DT, tpl.USE_TIME, tp.TRIP_PROD_TITLE, m.MEM_NM AS buyerName, p.PAY_DT, ROUND(s.NET_SALES * 0.1) AS commission, ROUND(s.NET_SALES * 0.9) AS settleAmt FROM SALES s JOIN TRIP_PROD_LIST tpl ON s.PROD_LIST_NO = tpl.PROD_LIST_NO JOIN TRIP_PROD tp ON tpl.TRIP_PROD_NO = tp.TRIP_PROD_NO JOIN PAYMENT p ON tpl.PAY_NO = p.PAY_NO JOIN MEMBER m ON p.MEM_NO = m.MEM_NO WHERE tp.MEM_NO = #{memNo}
AND s.SALE_DT >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND s.SALE_DT < TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND s.SETTLE_STAT_CD = #{status}
AND ( tp.TRIP_PROD_TITLE LIKE '%' || #{keyword} || '%' OR m.MEM_NM LIKE '%' || #{keyword} || '%' OR TO_CHAR(tpl.PAY_NO) LIKE '%' || #{keyword} || '%' )
ORDER BY s.SALE_DT DESC, s.SALE_NO DESC ) a
WHERE ROWNUM <= #{page} * #{pageSize}
)
WHERE rn > (#{page} - 1) * #{pageSize}

SELECT COUNT(*) FROM SALES s JOIN TRIP_PROD_LIST tpl ON s.PROD_LIST_NO = tpl.PROD_LIST_NO JOIN TRIP_PROD tp ON tpl.TRIP_PROD_NO = tp.TRIP_PROD_NO JOIN PAYMENT p ON tpl.PAY_NO = p.PAY_NO JOIN MEMBER m ON p.MEM_NO = m.MEM_NO WHERE tp.MEM_NO = #{memNo}
AND s.SALE_DT >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND s.SALE_DT < TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND s.SETTLE_STAT_CD = #{status}
AND ( tp.TRIP_PROD_TITLE LIKE '%' || #{keyword} || '%' OR m.MEM_NM LIKE '%' || #{keyword} || '%' OR TO_CHAR(tpl.PAY_NO) LIKE '%' || #{keyword} || '%' )

SELECT NVL(SUM(CASE WHEN s.SETTLE_STAT_CD != '취소' THEN s.NET_SALES ELSE 0 END), 0) AS netSales, NVL(SUM(CASE WHEN s.SETTLE_STAT_CD != '취소' THEN ROUND(s.NET_SALES * 0.1) ELSE 0 END), 0) AS commission, NVL(SUM(CASE WHEN s.SETTLE_STAT_CD != '취소' THEN ROUND(s.NET_SALES * 0.9) ELSE 0 END), 0) AS settleAmt FROM SALES s JOIN TRIP_PROD_LIST tpl ON s.PROD_LIST_NO = tpl.PROD_LIST_NO JOIN TRIP_PROD tp ON tpl.TRIP_PROD_NO = tp.TRIP_PROD_NO WHERE tp.MEM_NO = #{memNo}
AND s.SALE_DT >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND s.SALE_DT < TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND s.SETTLE_STAT_CD = #{status}
